require 'benchmark'
require 'benchmark/ips'

Benchmark.ips do |x|
  string = "aaaa|bbbbbbbbbbbbbbbbbbbbbbbbbbbb|cccccccccccccccccccccccccccccccccccc|dd|eeeeeeeeeeeeeeeeeeeeeeeeeeeeeee|ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff|gggggggggggggggggggggggggggggggggggggggggggggggggggg||i|j|k|l|m|n|ooooooooooooooooooooooooo|"

  x.report "string split, matching delimiter" do |times|
    i = 0
    while i < times
      string.split('|')
      i += 1
    end
  end

  x.report "string split, mismatched delimiter" do |times|
    i = 0
    while i < times
      string.split('.')
      i += 1
    end
  end
end
